    private void jbCargarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbCargarActionPerformed
        // TODO add your handling code here:
        try {
            if (jcCategoria.getSelectedIndex() == 0) {
                JOptionPane.showMessageDialog(this, "Debe seleccionar una categoria");
            } else if (jtProducto.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "El Campo detalle no puede quedar vacio");
            }
            String categoria = jcCategoria.getSelectedItem().toString();
            String producto = jtProducto.getText();
            double precio = Double.parseDouble(jtPrecio.getText());
            Productos produ = new Productos(categoria, producto, precio);
            cargarDatos(produ);

            jtProducto.setText("");
            jtPrecio.setText("");

        } catch (NumberFormatException nf) {
            JOptionPane.showMessageDialog(this, "La matricula debe ser un entero.");
        }
    }//GEN-LAST:event_jbCargarActionPerformed

    private void jbEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbEliminarActionPerformed
        // TODO add your handling code here:
        int filaS = jtProductos.getSelectedRow();
        if (filaS != (-1)) {
            modelo.removeRow(filaS);
        } else {
            JOptionPane.showMessageDialog(this, "Ud. no selecciono una fila");
        }


    }//GEN-LAST:event_jbEliminarActionPerformed

