    private void armarCabecera() {
        modelo.addColumn("Categoria");
        modelo.addColumn("Producto");
        modelo.addColumn("Precio");
        jtProductos.setModel(modelo);

    }

    private void cargarDatos(Productos productos) {
        modelo.addRow(new Object[]{productos.getCategoria(), productos.getProducto(), productos.getPrecio()});
    }

    private void cargarCombo() {
        jcCategoria.addItem("Categorias");
        jcCategoria.addItem("Electro");
        jcCategoria.addItem("Bazar");
        jcCategoria.addItem("Comestible");
        jcCategoria.addItem("Limpeza");
    }
